import React, { Component } from 'react';
import { Query } from 'react-apollo';
import gql from 'graphql-tag';

const ALL_ITEMS_QUERY = gql`
  query ALL_ITEMS_QUERY {
    # for this query, we're saying we want items and give these details of all the items
    items {
      id
      title
      price
      description
      image
      largeImage
    }
  }
`

export default class Items extends Component {
  render() {
    return (
      <div>
        Items!
        <Query query={ALL_ITEMS_QUERY}>
          {
            payload => {
              console.log(payload);
              return <p>Hey I am the ${payload} payload</p>
            }
          }
        </Query>
      </div>
    );
  }
}
