import React from 'react';
import { Mutation } from 'react-apollo';
import styled from 'styled-components';
import PropTypes from 'prop-types';
import gql from 'graphql-tag';
import { CURRENT_USER_QUERY } from './User';

const REMOVE_FROM_CART_MUTATION = gql`
  mutation removeFromCart($id: ID!) {
    removeFromCart(id: $id) {
      id
    }
  }
`;

const BigButton = styled.button`
  font-size: 3rem;
  background: none;
  border: 0;
  &:hover {
    color: ${props => props.theme.red};
    cursor: pointer;
  }
`;

class RemoveFromCart extends React.Component {
    static propTypes = {
        id: PropTypes.string.isRequired,
    };
    // This gets called as soon as we get a response back from the server after a mutation has been performed
    update = (cache, payload) => {
        console.log('Running remove from cart update fn');
        // 1. first read the cache
        const data = cache.readQuery({ query: CURRENT_USER_QUERY });
        console.log('data === ', data);
        console.log(' === payload', payload);
        // 2. remove that item from the cart
        const cartItemId = payload.data.removeFromCart.id;
        data.me.cart = data.me.cart.filter(cartItem => cartItem.id !== cartItemId);
        // 3. write it back to the cache
        cache.writeQuery({ query: CURRENT_USER_QUERY, data });
    };
    render() {
        return (
            <Mutation
                mutation={REMOVE_FROM_CART_MUTATION}
                // The line below; "variables={{ id: this.props.id }}" passes the id of the object to 
                // be deleted directly into the mutation here at the top
                // An alternative method would be to put "variables: this.props.id" in the button tag
                variables={{ id: this.props.id }}
                update={this.update}
                // OptimisticResponse here functions to allow for immediate U.I response to the 
                // removeFromCart function. There's usually some seconds of delay when an item is 
                // deleted from the cart. This is because the cart has to wait for the response from 
                // the server before the item is taken out of the UI. The optimisticResponse removes 
                // the item from the UI while the awaiting removal from the server.

                optimisticResponse={{
                    __typename: 'Mutation',
                    removeFromCart: {
                        __typename: 'CartItem',
                        id: this.props.id,
                    },
                }}
            >
                {(removeFromCart, { loading, error }) => (
                    <BigButton
                        disabled={loading}
                        onClick={() => {
                            removeFromCart().catch(err => alert(err.message));
                        }}
                        title="Delete Item"
                    >
                        &times;
          </BigButton>
                )}
            </Mutation>
        );
    }
}

export default RemoveFromCart;
