import React, { Component } from 'react';
import { Mutation } from 'react-apollo';
import gql from 'graphql-tag';
import Router from 'next/router';
import Form from './styles/Form';
import formatMoney from '../lib/formatMoney';
import Error from './ErrorMessage';


//This is the mutation done to push the submitted data to the graphql server
// It is exported at the bottom for use in other files
// Basically, this function takes in pre-set variables and creates an item that contains the 
// specified variables as its property
const CREATE_ITEM_MUTATION = gql`
  mutation CREATE_ITEM_MUTATION(
    $title: String!
    $description: String!
    $price: Int!
    $image: String
    $largeImage: String
  ) {
    #The createItem name below is gotten from the graphql schema at the backend; its the called 
    # endpoint on the server side
    createItem(
      title: $title
      description: $description
      price: $price
      image: $image
      largeImage: $largeImage
    ) {
        # We need the id of the item created; hence, id below
      id
    }
  }
`;

class CreateItem extends Component {
    // Although Apollo manages the state, state here is used to manage initial state locally
    // This is because data needs to be stored locally before been sent to the graphql API
    state = {
        title: '',
        description: '',
        image: '',
        largeImage: '',
        price: 0,
    };
    handleChange = e => {
        const { name, type, value } = e.target;
        const val = type === 'number' ? parseFloat(value) : value;
        this.setState({ [name]: val }); //Here, we're using computed property names (es6 destructuring)
        //so the handleChange function can be used for more than one input field
    };

    uploadFile = async e => {
        // console.log('uploading file...');
        const files = e.target.files;
        const data = new FormData();
        data.append('file', files[0]);
        data.append('upload_preset', 'sickfits');

        const res = await fetch('https://api.cloudinary.com/v1_1/doednsebg/image/upload', {
            method: 'POST',
            body: data,
        });
        const file = await res.json();
        // console.log(file);
        this.setState({
            image: file.secure_url,
            largeImage: file.eager[0].secure_url,
        });
    };


    render() {
        return (
            <Mutation mutation={CREATE_ITEM_MUTATION} variables={this.state}>
                {(createItem, { loading, error }) => (
                    //This is an implicit return .i.e. It doesnt need a return keyword
                    <Form 
                        data-test = "form"
                        onSubmit={async e => {
                            // Stop the form from submitting
                            e.preventDefault();
                            // call the mutation
                            const res = await createItem(); //res here means response
                            // change them to the single item page
                            console.log('response=======>', res);
                            Router.push({
                                pathname: '/item',
                                query: { id: res.data.createItem.id },
                            });
                        }}
                    >
                    {/* The error msg is in the ErrorMessage file & is from apollo  */}
                        <Error error={error} />
                        {/* Fieldset used here allows us to disable the form if its loading.
                        Hence, it takes the loading parameter in disabled mode
                        Aria-busy attribute allows the padding at the top of form to mimic a loading feature */}
                        <fieldset disabled={loading} aria-busy={loading}>
                        {/* 'htmlFor' is used for labels because 'for' is a reserved word in react */}
                            <label htmlFor="file">
                                Image
                                <input
                                    type="file"
                                    id="file"
                                    name="file"
                                    placeholder="Upload an image"
                                    required
                                    onChange={this.uploadFile}
                                />
                                {this.state.image && (
                                    <img width="200" src={this.state.image} alt="Upload Preview" />
                                )}
                            </label>

                            <label htmlFor="title">
                                Title
                                <input
                                    type="text"
                                    id="title"
                                    name="title"
                                    placeholder="Title"
                                    required
                                    value={this.state.title}
                                    onChange={this.handleChange}
                                />
                            </label>

                            <label htmlFor="price">
                                Price
                                <input
                                    type="number"
                                    id="price"
                                    name="price"
                                    placeholder="Price"
                                    required
                                    value={this.state.price}
                                    onChange={this.handleChange}
                                />
                            </label>

                            <label htmlFor="description">
                                Description
                                <textarea
                                    id="description"
                                    name="description"
                                    placeholder="Enter A Description"
                                    required
                                    value={this.state.description}
                                    onChange={this.handleChange}
                                />
                            </label>
                            <button type="submit">Submit</button>
                        </fieldset>
                    </Form>
                )}
            </Mutation>
        );
    }
}

export default CreateItem;
export { CREATE_ITEM_MUTATION };
